import React, { useEffect, useState } from "react";
import "./Notes.css"; // We'll create a separate CSS for styling

function Notes() {
  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [user, setUser] = useState(null);
  const [tenantSlug, setTenantSlug] = useState("");
  const [inviteEmail, setInviteEmail] = useState("");
  const [inviteRole, setInviteRole] = useState("MEMBER");

  const token = localStorage.getItem("token");

  useEffect(() => {
    // decode token to get role and tenantSlug
    if (token) {
      const payload = JSON.parse(atob(token.split(".")[1]));
      setUser({ id: payload.userId, role: payload.role });
      setTenantSlug(payload.tenantSlug);
    }

    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    const res = await fetch("/api/notes", {
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await res.json();
    setNotes(data.notes);
  };

  const createNote = async () => {
    if (!title) return alert("Title is required");
    const res = await fetch("/api/notes", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ title, content }),
    });
    const data = await res.json();
    if (data.error) return alert(data.error);
    setTitle("");
    setContent("");
    fetchNotes();
  };

  const deleteNote = async (id) => {
    const res = await fetch(`/api/notes/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await res.json();
    fetchNotes();
  };

  // === Admin Actions ===
  const inviteUser = async () => {
    if (!inviteEmail) return alert("Enter email");
    const res = await fetch("/api/users/invite", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ email: inviteEmail, role: inviteRole }),
    });
    const data = await res.json();
    alert(data.message);
    setInviteEmail("");
  };

  const upgradeTenant = async () => {
    const res = await fetch(`/api/tenants/${tenantSlug}/upgrade`, {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await res.json();
    alert(data.message);
  };

  return (
    <div className="notes-container">
      <h1>Notes Dashboard</h1>

      <div className="create-note">
        <input
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <textarea
          placeholder="Content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
        <button onClick={createNote}>Add Note</button>
      </div>

      <div className="notes-list">
        {notes.map((note) => (
          <div className="note-card" key={note._id}>
            <h3>{note.title}</h3>
            <p>{note.content}</p>
            <button onClick={() => deleteNote(note._id)}>Delete</button>
          </div>
        ))}
      </div>

      {user?.role === "ADMIN" && (
        <div className="admin-panel">
          <h2>Admin Actions</h2>

          <div className="invite-user">
            <input
              type="email"
              placeholder="User email"
              value={inviteEmail}
              onChange={(e) => setInviteEmail(e.target.value)}
            />
            <select
              value={inviteRole}
              onChange={(e) => setInviteRole(e.target.value)}
            >
              <option value="MEMBER">Member</option>
              <option value="ADMIN">Admin</option>
            </select>
            <button onClick={inviteUser}>Invite User</button>
          </div>

          <div className="upgrade-tenant">
            <button onClick={upgradeTenant}>Upgrade Tenant to PRO</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Notes;
