// frontend/src/pages/AdminDashboard.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";

function AdminDashboard({ user }) {
  const [users, setUsers] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [role, setRole] = useState("MEMBER");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const token = localStorage.getItem("token");

  // Fetch all users in the tenant
  const fetchUsers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/admin/users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Add a new user
  const addUser = async () => {
    if (!name || !email || !role || !password) {
      setError("All fields are required");
      return;
    }
    try {
      await axios.post(
        "http://localhost:5000/api/admin/users",
        { name, email, role, password },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setName("");
      setEmail("");
      setPassword("");
      setRole("MEMBER");
      setError("");
      fetchUsers();
    } catch (err) {
      setError(err.response?.data?.message || "Error adding user");
    }
  };

  // Delete user
  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/users/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchUsers();
    } catch (err) {
      console.error(err);
    }
  };

  // Upgrade user plan
  const upgradeUser = async (id) => {
    try {
      await axios.post(
        `http://localhost:5000/api/admin/${id}/upgrade`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchUsers();
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>

      <div className="add-user-form">
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="MEMBER">Member</option>
          <option value="ADMIN">Admin</option>
        </select>
        <button onClick={addUser}>Add User</button>
        {error && <p className="error">{error}</p>}
      </div>

      <div className="users-list">
        {users.map((u) => (
          <div key={u._id} className="user-card">
            <span>{u.name} - {u.email} - {u.role}</span>
            <button onClick={() => deleteUser(u._id)}>Delete</button>
            {u.role === "MEMBER" && (
              <button onClick={() => upgradeUser(u._id)}>Upgrade to PRO</button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default AdminDashboard;
