import React, { useEffect, useState } from "react";

function Notes() {
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [error, setError] = useState("");
  const [plan, setPlan] = useState("FREE"); // default to FREE
  const [noteCount, setNoteCount] = useState(0);

  const token = localStorage.getItem("token"); // get JWT from login

  // Fetch notes from backend
  const fetchNotes = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/notes", {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await res.json();

      if (res.status !== 200) throw new Error(data.message || "Failed to fetch notes");

      setNotes(data.notes || data); // handle data format
      setNoteCount(data.notes ? data.notes.length : (data.length || 0));
      setPlan(data.plan || "FREE");
    } catch (err) {
      console.error(err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  // Create new note
  const handleAddNote = async (e) => {
    e.preventDefault();
    setError("");

    if (!title || !content) {
      setError("Title and content are required");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/notes", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ title, content }),
      });

      const data = await res.json();

      if (!res.ok) {
        if (data.upgradeRequired) {
          setError("Note limit reached. Please upgrade to Pro.");
        } else {
          setError(data.message || "Failed to create note");
        }
        return;
      }

      // Add the new note to the list
      setNotes([data, ...notes]);
      setNoteCount(noteCount + 1);
      setTitle("");
      setContent("");
    } catch (err) {
      console.error(err);
      setError("Error creating note");
    }
  };

  if (loading) return <p>Loading notes...</p>;

  return (
    <div style={{ maxWidth: "600px", margin: "0 auto", padding: "1rem" }}>
      <h2>Notes Dashboard</h2>
      <p>Plan: {plan} | Notes: {noteCount}</p>

      {error && <p style={{ color: "red" }}>{error}</p>}

      <form onSubmit={handleAddNote} style={{ marginBottom: "1rem" }}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{ width: "100%", marginBottom: "0.5rem" }}
        />
        <textarea
          placeholder="Content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          style={{ width: "100%", marginBottom: "0.5rem" }}
        />
        <button type="submit">Add Note</button>
      </form>

      {notes.length === 0 ? (
        <p>No notes found.</p>
      ) : (
        <ul>
          {notes.map((note, idx) => (
            <li key={idx} style={{ marginBottom: "0.5rem" }}>
              <strong>{note.title}</strong>
              <p>{note.content}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Notes;
